function update()
end


function draw_overlay()
	vbank(1)
	cls()
	
	for i=1,t%110>55 and 13 or 3 do
		trib(math.random(240),
		     math.random(136),
			 			math.random(240),
		     math.random(136),
					 	math.random(240),
		     math.random(136),
						 math.random(16))
	end
	
	vbank(0)
end

-------------------------------------
-- GAME LOOP                       --
-------------------------------------

t=0

function BOOT()
	music(0)
end

-- try to justify the "scene" part
-- of the cartridge name
function BDR(scanline)
	-- palette fuckery
	local col = 1-(t/30)*190
	poke(0x3FDB, col-1.1*scanline)
	poke(0x3FDC, col-1.2*scanline+t)
	poke(0x3FDD, col-2*scanline)
	-- wavy raster effects
	local ox = 10 * math.sin(
	            0.1 * (scanline  + 2*t))
	local oy = 6 * math.cos(
	            0.07 * (scanline + 2*t))
	if t%110>55 then
		ox = ox * (1-(t % 110 / 15))
		oy = 136-(t%110/35)*scanline-oy
	end
	offset_screen(ox, oy)
end

function TIC()
	--------- update ----------
	
	update()
	
	---------- draw -----------
	
	vbank(0)
	
	-- dirt
	map()
	-- the sun
	circ(230, 0, 20, 4)
	-- dino
	spr(48, 20, 24, 0, 1, 0, 0, 3, 3)
	line(42, 32, 55, 26, 0)
	print("gwaaaaaaarrgh", 56, 20, 0)
	-- dude
	spr(83, 120, 40, 15)
		
	draw_overlay()
	
	--------- advance t ----------
	
	t = t + 1
end
